import React, { useState } from 'react';

const AdminGrid = ({ articles, onDelete, onSelectForCarousel }) => {
  const [selectedArticles, setSelectedArticles] = useState([]);

  const handleSelectArticle = (articleId) => {
    if (selectedArticles.includes(articleId)) {
      setSelectedArticles(selectedArticles.filter(id => id !== articleId));
    } else {
      if (selectedArticles.length < 6) { // Limit to 6 selected articles
        setSelectedArticles([...selectedArticles, articleId]);
      } else {
        alert('You can only select 6 articles for the carousel');
      }
    }
  };

  const handleDelete = (articleId) => {
    onDelete(articleId);
  };

  return (
    <div>
      <h2>Article Grid</h2>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Category</th>
            <th>Published Date</th>
            <th>Author</th>
            <th>Actions</th>
            <th>Select for Carousel</th>
          </tr>
        </thead>
        <tbody>
          {articles.map(article => (
            <tr key={article.id}>
              <td>{article.title}</td>
              <td>{article.category}</td>
              <td>{article.publishedDate}</td>
              <td>{article.author}</td>
              <td>
                <button onClick={() => handleDelete(article.id)}>Delete</button>
              </td>
              <td>
                <input 
                  type="checkbox" 
                  checked={selectedArticles.includes(article.id)} 
                  onChange={() => handleSelectArticle(article.id)} 
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminGrid;
