import React, { useState, useEffect } from 'react';


  

const AdminGrid = ({ onDeleteArticle, onSelectArticle }) => {
    const articles = [
        {
          id: 1,
          title: "Introduction to React",
          category: "Technology",
          publishedDate: "2024-12-15",
          author: "John Doe",
          selected: false,
          imageUrl: "https://via.placeholder.com/150",  // Example image URL
        },
        {
          id: 2,
          title: "The Future of Web Development",
          category: "Technology",
          publishedDate: "2024-12-10",
          author: "Jane Smith",
          selected: false,
          imageUrl: "https://via.placeholder.com/150",
        },
        {
          id: 3,
          title: "A Deep Dive into Machine Learning",
          category: "AI & ML",
          publishedDate: "2024-11-25",
          author: "Alice Johnson",
          selected: false,
          imageUrl: "https://via.placeholder.com/150",
        },
        {
          id: 4,
          title: "10 Tips for Better Web Performance",
          category: "Web Development",
          publishedDate: "2024-12-05",
          author: "Bob Lee",
          selected: false,
          imageUrl: "https://via.placeholder.com/150",
        },
        {
          id: 5,
          title: "Understanding Blockchain Technology",
          category: "Blockchain",
          publishedDate: "2024-11-30",
          author: "Charlie Brown",
          selected: false,
          imageUrl: "https://via.placeholder.com/150",
        },
        {
          id: 6,
          title: "How to Build a Personal Portfolio Website",
          category: "Web Development",
          publishedDate: "2024-12-01",
          author: "David Green",
          selected: false,
          imageUrl: "https://via.placeholder.com/150",
        },
        {
          id: 7,
          title: "Exploring Quantum Computing",
          category: "Technology",
          publishedDate: "2024-11-20",
          author: "Eve White",
          selected: false,
          imageUrl: "https://via.placeholder.com/150",
        },
        {
          id: 8,
          title: "Mastering JavaScript ES6 Features",
          category: "Programming",
          publishedDate: "2024-12-18",
          author: "Frank Harris",
          selected: false,
          imageUrl: "https://via.placeholder.com/150",
        },
        {
          id: 9,
          title: "An Introduction to Python for Data Science",
          category: "Data Science",
          publishedDate: "2024-11-28",
          author: "Grace Wilson",
          selected: false,
          imageUrl: "https://via.placeholder.com/150",
        },
        {
          id: 10,
          title: "Cloud Computing: Benefits and Challenges",
          category: "Cloud Computing",
          publishedDate: "2024-12-10",
          author: "Henry Martin",
          selected: false,
          imageUrl: "https://via.placeholder.com/150",
        }
      ];
  const handleDelete = (articleId) => {
    onDeleteArticle(articleId);
  };

  const handleSelect = (articleId) => {
    onSelectArticle(articleId);
  };

  return (
    <div className="admin-grid">
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Category</th>
            <th>Published Date</th>
            <th>Author</th>
            <th>Actions</th>
            <th>Select for Carousel</th>
          </tr>
        </thead>
        <tbody>
          {articles.map((article) => (
            <tr key={article.id}>
              <td>{article.title}</td>
              <td>{article.category}</td>
              <td>{article.publishedDate}</td>
              <td>{article.author}</td>
              <td>
                <button onClick={() => handleDelete(article.id)}>Delete</button>
              </td>
              <td>
                <input
                  type="checkbox"
                  onChange={() => handleSelect(article.id)}
                  checked={article.selected}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminGrid;
