import React, { useState, useEffect } from "react";

// Mock service for article operations (can be replaced with actual API calls)
const mockArticleService = {
  getArticles: async () => {
    // Fetch existing articles (this is a mock, replace with actual API call)
    return Array.from({ length: 100 }, (_, index) => ({
      id: index + 1,
      title: `Article ${index + 1}`,
      category: `Category ${Math.floor(Math.random() * 10) + 1}`,
      publishedDate: `2025-01-${(index % 31) + 1}`,
      author: `Author ${Math.floor(Math.random() * 5) + 1}`,
      views: Math.floor(Math.random() * 500),
    }));
  },
  deleteArticle: async (articleId) => {
    console.log("Article Deleted: ", articleId);
  },
};

export default function AdminGrid() {
  const [articles, setArticles] = useState([]);
  const [selectedArticles, setSelectedArticles] = useState([]);
  const [searchTerm, setSearchTerm] = useState(""); // New state for search term
  const [currentPage, setCurrentPage] = useState(1);
  const articlesPerPage = 10;

  // Fetch articles on component mount
  useEffect(() => {
    const fetchArticles = async () => {
      const data = await mockArticleService.getArticles();
      setArticles(data);
    };
    fetchArticles();
  }, []);

  // Filter articles based on search term
  const filteredArticles = articles.filter((article) =>
    article.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Calculate the articles to display based on the current page and search term
  const indexOfLastArticle = currentPage * articlesPerPage;
  const indexOfFirstArticle = indexOfLastArticle - articlesPerPage;
  const currentArticles = filteredArticles.slice(indexOfFirstArticle, indexOfLastArticle);

  // Handle deleting an article
  const handleDeleteArticle = async (articleId) => {
    await mockArticleService.deleteArticle(articleId);
    setArticles((prevArticles) => prevArticles.filter((article) => article.id !== articleId));
  };

  // Handle selecting articles for the carousel
  const handleSelectArticle = (articleId) => {
    if (selectedArticles.includes(articleId)) {
      setSelectedArticles(selectedArticles.filter((id) => id !== articleId));
    } else {
      if (selectedArticles.length < 6) {
        setSelectedArticles([...selectedArticles, articleId]);
      } else {
        alert("You can only select 6 articles for the carousel.");
      }
    }
  };

  // Handle page change
  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  // Calculate the total number of pages
  const totalPages = Math.ceil(filteredArticles.length / articlesPerPage);

  return (
    <div className="article-container">
      <h1>Manage Articles</h1>

      {/* Search Field */}
      <div className="search-articles">
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Search articles"
        />
      </div>

      {/* Article List with Delete and Select for Carousel Options */}
      <div className="article-list">
        <div className="row">
          <div className="col-md-9 col-sm-6">
            <h2 style={{ display: "inline" }}>Existing Articles</h2>
          </div>
          <div className="col-md-3 col-sm-6" style={{ textAlign: "end" }}>
            {/* Search Field */}
          </div>
        </div>
        <ul>
          {currentArticles.map((article) => (
            <li key={article.id}>
              <div>
                <strong>{article.title}</strong>
                <p>{article.category} | {article.publishedDate} | {article.author}</p>
                <button onClick={() => handleDeleteArticle(article.id)}>Delete</button>
                <input
                  type="checkbox"
                  checked={selectedArticles.includes(article.id)}
                  onChange={() => handleSelectArticle(article.id)}
                />
                Select for Carousel
              </div>
            </li>
          ))}
        </ul>
      </div>

      {/* Pagination Controls */}
      <div className="article-pagination">
        <button onClick={() => handlePageChange(currentPage - 1)} disabled={currentPage === 1}>
          Previous
        </button>
        <span>
          Page {currentPage} of {totalPages}
        </span>
        <button onClick={() => handlePageChange(currentPage + 1)} disabled={currentPage === totalPages}>
          Next
        </button>
      </div>
    </div>
  );
}
