import axios from 'axios';
import apiService from '../client/client';

const getHomeData = async (data = {}) => {
  try {
    // Axios call to the backend API
    const result = await apiService.get('')
    //const result = await axios.get('http://localhost:3000/api/home');
    
    // Return the result if successful
    return result.data;
  } catch (error) {
    // Handle errors
    console.error('Error in getHomeData:', error);
    
    // You can return an error message or handle it based on your need
    throw new Error('Failed to fetch home data');
  }
};

export { getHomeData };
